mapper.select:
SELECT * FROM MEMBER
WHERE USER_ID = #{userId}
		AND STATUS IN('Y','A')

mapper.update:
UPDATE MEMBER
SET NICK_NAME = #{nickName}
, ADDRESS = #{address}
WHERE USER_ID = #{userId} AND STATUS IN ('Y','A')

UPDATE MEMBER
SET p_IMG = #{pfImg}
, introduce = #{introduce}
WHERE USER_ID = #{userId} AND STATUS IN ('Y','A')
